// Fix Firefox iframe cache bug. Cf. https://bugzilla.mozilla.org/show_bug.cgi?id=356558
$(document).ready(function() {
    $('iframe').each(function () {
        $(this)[0].contentWindow.location.href = $(this)[0].src;
    });
});

function BookmarkManager(params, toggle_action_click, toggle_action_hover, toggle_request, find_one_request){

    var that = this;
    this.request = null;
    this.devController = window.location.href.indexOf('/app_dev.php')>-1?'/app_dev.php':'';
    this.active_class = (params && params.hasOwnProperty('active_class'))?params.active_class:"glyphicon-heart";
    this.passive_class = (params && params.hasOwnProperty('passive_class'))?params.passive_class:"glyphicon-heart-empty";
    this.toggle_action_click = toggle_action_click;
    this.toggle_action_hover = toggle_action_hover;
    this.toggle_request = toggle_request;
    this.find_one_request = find_one_request;
    this.find_all_url = (params && params.hasOwnProperty('find_all_url'))?params.find_all_url:this.devController+'/favoris/findAll';
    this.toggle_request_url = (params && params.hasOwnProperty('toggle_request_url'))?params.toggle_request_url:this.devController+'/favoris/toggleOne';
    this.find_one_url = (params && params.hasOwnProperty('find_one_url'))?params.find_one_url:this.devController+'/favoris/find_one_url';
    this.bookmarks = {};
    //Default URL for single bookmark page
    this.default_url = window.location.href;

    this.cleanUrl = function(url){
        //Avoid anchors in the link ("http://element#top" == "http://element")
        if(url.indexOf("#") > -1 ){
            url = url.substring(0, url.indexOf("#"));
        }
        return url;
    };

    this.enableBookmarks = function(check_in_db){
        let unclassedBookmarks = document.querySelectorAll('[data-bookmark-id]');
        let bookmarks_json = {};
        for(let i = 0; i < unclassedBookmarks.length; ++i){
            let unclassedBookmark = unclassedBookmarks[i];
            let bookmarkID = unclassedBookmark.getAttribute("data-bookmark-id");
            let bookmarkType = unclassedBookmark.getAttribute("data-bookmark-type");
            if(!this.bookmarks[bookmarkID]){
                this.bookmarks[bookmarkID] = {};
                this.bookmarks[bookmarkID][bookmarkType] = unclassedBookmark;
            }
            else{
                this.bookmarks[bookmarkID][bookmarkType] = unclassedBookmark;
            }
        }

        //that is using here to avoid the context error in CB
        for(let bookmark in that.bookmarks){
            let title = that.bookmarks[bookmark].title.innerText;
            let url = (that.bookmarks[bookmark].url)?that.bookmarks[bookmark].url.href:that.default_url;
            let image_url = (that.bookmarks[bookmark].image)?that.bookmarks[bookmark].image.src:"";
            $(that.bookmarks[bookmark].button).hover(function(event){
                that.toggle($(this), event, title, url, image_url);
            });
            $(that.bookmarks[bookmark].button).on('click', function(event){
                that.toggle($(this), event, title, url, image_url);
                event.stopPropagation();
            });
            bookmarks_json[bookmark] = this.cleanUrl(url);
        }
        if(check_in_db){
            that.findAll(bookmarks_json);
        }
    };

    this.findOne = function(url, element){
        url = this.cleanUrl(url);
        this.request = (this.find_one_request)?this.find_one_request(url):this.defaultFindOneRequest(url);
        $.ajax({
            type: 'GET',
            url: this.request,
            success: function() {
                element.removeClass(that.passive_class).addClass(that.active_class);
            }
        });
    };

    this.defaultFindOneRequest = function(url){
        return that.find_one_url +"?url="+encodeURIComponent(url)
    };


    this.findAll = function(bookmarks) {
        $.ajax({
            type: 'POST',
            url: that.find_all_url,
            dataType: 'json',
            contentType: 'application/json',
            data: JSON.stringify(bookmarks),
            success: function(data) {
                for(let id in data) {
                    if(data[id] === "200"){
                        $(that.bookmarks[id].button).removeClass(that.passive_class).addClass(that.active_class);
                    }
                }
            }
        });
    };

    this.toggle = function(element, event,title, url, image_url) {
        if(event.type === "click"){
            (this.toggle_action_click)?this.toggle_action_click(element, event):this.defaultToggleActionClick(element, event);
            url = this.cleanUrl(url);
            this.request = (this.toggle_request)?this.toggle_request(title, url, image_url):this.defaultToggleRequest(title, url, image_url);
            $.ajax({
                type: 'GET',
                url: this.request
            });
        }else{
            (this.toggle_action_hover)?this.toggle_action_hover(element, event):this.defaultToggleActionHover(element, event);
        }
    };

    this.defaultToggleActionClick = function(element, event){
        element.toggleClass(this.passive_class).toggleClass(this.active_class);
    };

    this.defaultToggleActionHover = function(element, event){
        element.toggleClass(this.passive_class).toggleClass(this.active_class);
    };

    this.defaultToggleRequest = function(title, url, image_url){
        return that.toggle_request_url +"?title="+title+"&url="+encodeURIComponent(url)+"&image_url="+encodeURIComponent(image_url);
    };

    this.reset = function(check_in_db) {
        if(check_in_db === undefined) {
            check_in_db = true;
        }
        for(let bookmark in that.bookmarks) {
            $(that.bookmarks[bookmark].button).off();
        }
        this.enableBookmarks(check_in_db);
    };

}

function infiniteScroll(params) {
    /**
     * Usage: params is an object with the following properties:
     * {
     *     page: the current page number (int)
     *     maxPage: the maximum number of pages (int)
     *     container: a jQuery selector for the element containing the items
     *     selector: a jQuery selector for the items
     *     loader?: a jQuery selector for a loader to show/hide
     *     pagination?: a jQuery selector for a pagination that will be hidden
     *     afterLoader?: a function that will be executed every time new content is loaded
     * }
     */
    if(!params instanceof Object) {
        throw new Error('params must be an object');
    }
    let requiredParameters = ['page', 'maxPage', 'container', 'selector'];
    for(let i in requiredParameters) {
        if(!params.hasOwnProperty(requiredParameters[i])) {
            throw new Error('Parameter '+requiredParameters['i']+' is required');
        }
    }

    const that = this;
    this.pollingData = false;
    this.currentPage = params.page;
    this.initialPage = params.page;
    this.maxPage = params.maxPage;
    this.container = $(params.container);
    this.selector = params.selector;
    this.loader = params.hasOwnProperty('loader')?$(params.loader):null;
    this.paginationContainer = params.hasOwnProperty('pagination')?$(params.pagination):null;
    this.afterLoading = params.hasOwnProperty('afterLoading')?params.afterLoading:null;

    if(this.paginationContainer) {
        this.paginationContainer.hide();
    }

    jQuery(document).scroll(function(){
        const distToBottom = getDistFromBottom();
        const lastBlock = that.container.find(that.selector + ':last');
        const bottomToLastArticle = document.body.offsetHeight - lastBlock.position().top;
        if(that.pollingData || distToBottom < 0 || distToBottom > bottomToLastArticle || that.currentPage >= that.maxPage) {
            return;
        }

        that.loadNextPage();
    });

    // Loads pages until all pages are loaded or the document can scroll
    const readyScroll = function readyScroll() {
        if(getDistFromBottom() <= 5 && that.currentPage < that.maxPage) {
            that.loadNextPage(readyScroll);
        }
    };
    jQuery(document).ready(readyScroll);

    this.loadNextPage = function(done) {
        that.pollingData = true;
        if(that.loader) {
            that.loader.show();
        }
        let url = window.location.search.replace('&page='+that.initialPage, '&page='+(that.currentPage+1));
        if(url.indexOf('&page=')===-1) {
            url+= '&page='+(that.currentPage+1);
        }
        jQuery.get(url)
            .done(function(res) {
                that.currentPage++;
                const $res = $(res);
                $res.find(that.selector).appendTo(that.container);
                if(that.currentPage === that.maxPage) {
                    that.container.after('<span>Fin des résultats</span>');
                }
                if(that.afterLoading) {
                    that.afterLoading();
                }
                if(done && done instanceof Function) {
                    done();
                }
            })
            .always(function(){
                // Delay the next event
                setTimeout(function(){
                    that.pollingData = false;
                }, 500);
                if(that.loader) {
                    that.loader.hide();
                }
            })
        ;
    };

    function getDistFromBottom () {
        const scrollPosition = window.pageYOffset;
        const windowSize     = window.innerHeight;
        const bodyHeight     = document.body.offsetHeight;

        return Math.max(bodyHeight - (scrollPosition + windowSize), 0);
    }
}

function getExportStatus(id, htmlTargetId)
{
    const htmlTarget = document.getElementById(htmlTargetId);
    htmlTarget.innerHTML = 'Export en cours...<br />Veuillez ne pas quitter la page, le statut se rafraîchit automatiquement.';
    const base = (window.location.href.indexOf('/app_dev.php')>-1?'/app_dev.php':'') +'/moncompte/export/';
    try {
        fetch(base + id + '/status').then((response) => {
            if (!response.ok) {
                htmlTarget.innerHTML = 'Impossible de récupérer votre export';
                return Promise.reject(response);
            }
            return response.json();
        }).then((response) => {
            if (response.status === 'new' || response.status === 'processing') {
                setTimeout(function() { getExportStatus(id, htmlTargetId); }, 5000);
                return 0;
            } else if (response.status === 'ready') {
                let downloadButton = document.createElement('a');
                downloadButton.target = '_blank';
                downloadButton.href = base + id + '/download';
                downloadButton.classList.add('btn', 'btn-default')
                downloadButton.text = 'Télécharger le fichier';
                htmlTarget.innerHTML = 'Votre export est prêt.';
                htmlTarget.appendChild(downloadButton);
            } else {
                htmlTarget.innerHTML = 'Impossible de récupérer votre export';
            }
            return response.status;
        });
    }
    catch(e) {
        console.log(e);
    }
    return 0;
}

